import React, { useState } from "react";
import { Link } from "react-router-dom";


const HeaderComponent = () => {
  const [isClicked, setIsClicked] = useState(false);

  return (
    <div>
      <ul className="header">
        <div className="logo">
          <input
            className="radio-1"
            type="radio"
            id="1-option"
            name="selector"
            onClick={() => setIsClicked(!isClicked)}
          />

          <Link
            to={"/employee"}
            style={{ textDecoration: "none" }}
            className="text-white"
            href=""
          >
            Employee
          </Link>

          <Link
            to={"/add-employee"}
            style={{ textDecoration: "none" }}
            className="text-white hover:nav-blue"
            href=""
          >
            +
          </Link>
        </div>
        <li>Position</li>
        <li>Location</li>
        <li>Salary</li>
        <li>Actions</li>
      </ul>
      {isClicked}
    </div>
  );
};

export default HeaderComponent;
