import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import EmployeeService from '../service/EmployeeService';

const ListEmployeeComponent = () => {
    const [employeeArray, setEmployeeArray] = useState([]);

    useEffect(() => {
        getAllEmployee();
    }, []);

    function getAllEmployee() {
        EmployeeService.getAllEmployee()
            .then(res => { setEmployeeArray(res.data); console.log(res) })
            .catch(e => console.log(e));
    }
    function deleteEmployee(e, id) {
        e.preventDefault()
        EmployeeService.deleteEmployee(id).then(getAllEmployee()).catch(e => console.log(e));
    }


    return (
      <>
        <div className=""></div>

        <div className="container">
          {/* <Link to={"/add-employee"} style={{ textDecoration: "none" }} href="">
            Add Employee
          </Link> */}
          <table className="table table-bordered table striped w-full">
            <thead>
              <th>Employee ID</th>
              <th>Employee First Name</th>
              <th>Employee Last Name</th>
              <th>Employee Email</th>
              <th>Actions</th>
            </thead>
            <tbody>
              {employeeArray.map((employee) => (
                <tr id={employee.id}>
                  <td>{employee.id}</td>
                  <td>{employee.firstName}</td>
                  <td>{employee.lastName}</td>
                  <td>{employee.email}</td>
                  <td>
                    <Link
                      to={`/add-employee/${employee.id}`}
                      className="btn btn-info"
                      href=""
                    >
                      Update
                    </Link>{" "}
                    <a
                      onClick={(e) => deleteEmployee(e, employee.id)}
                      className="btn btn-danger"
                    >
                      Delete
                    </a>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </>
    );
}

export default ListEmployeeComponent
