import React, { useState } from "react";

const HeaderComponent = () => {
  const [isClicked, setIsClicked] = useState(false);

  return (
    <div>
      <ul className="header">
        <div className="logo">
          <input
            className="radio-1"
            type="radio"
            id="1-option" 
            name="selector"
            onClick={() => setIsClicked(!isClicked)}
          />
          <li>
            <label htmlFor="1-option">Employee</label>{" "}
          </li>
        </div>
        <li>Position</li> 
        <li>Location</li>
        <li>Salary</li>
        <li>Actions</li>
      </ul>
      {isClicked && <div>DropDown</div>}
    </div>
  );
};

export default HeaderComponent;
