import React, { useState } from "react";
import { Link } from "react-router-dom";


const HeaderComponent = () => {
  const [isClicked, setIsClicked] = useState(false);

  return (
    <div>
      <ul className="header">
        <div className="logo">
          <input
            className="radio-1"
            type="radio"
            id="1-option" 
            name="selector"
            onClick={() => setIsClicked(!isClicked)}
          />
          <li>
            <label htmlFor="1-option">Employee</label>{" "}
                  </li>
                   <Link
            to={"/add-employee"}
            className="border-4 bg-white py-3 px-2"
            href=""
          >+</Link>
        </div>
        <li>Position</li> 
        <li>Location</li>
        <li>Salary</li>
        <li>Actions</li>
      </ul>
      {isClicked}
    </div>
  );
};

export default HeaderComponent;
