import React, { useState } from "react";

const HeaderComponent = () => {
  const [isClicked, setIsClicked] = useState(false);

  return (
    <div>
      <ul className="header list-none">
        <li>
          <input
            className="mr-[20px] border border-red-500"
            type="radio"
            name="selector"
            onClick={() => setIsClicked(!isClicked)}
          />
          <label for="1-option">Employee</label>
        </li>
        <li>Postion</li>
        <li>Location</li>
        <li>Salary</li>
        <li>Actions</li>
      </ul>
      {isClicked && <div>DropDown</div>}
    </div>
  );
};

export default HeaderComponent;
