import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import UserService from "../service/UserService";
import "../styles/add.css";

const AddEmployeeComponent = () => {
  /** Variables and method to collect and store inputes */
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [location, setLocation] = useState("");
  const [hours, setHours] = useState("");
  const [position, setPosition] = useState("");
  const navigate = useNavigate();
  const userId = localStorage.getItem("userId"); 
  // const employeeId = localStorage.getItem("employeeId")

  const employeeData = {
    firstName,
    lastName,
    email,
    location,
    position,
    hours,
  };

  function saveEmployee(e) {
    e.preventDefault();
    if (Object.values(employeeData).every((field) => field !== "")) {
      UserService.addEmployee(userId, employeeData)
        .then((response) => {
          localStorage.setItem('employeeId', response.data.id)
          navigate(`/user/employees`)
        } 
      )
        .catch((e) => console.log(e));
    } else {
      alert("Please fill in all fields");
    }
  }

  // function tile() {
  //   if (employeeId) {
  //     return "Update Employee";
  //   } else {
  //     return "Add Employee";
  //   }
  // }
  useEffect(() => {
    if (userId) {
      console.log(userId)
      UserService.getEmployeeById(userId)
        .then((res) => {
          setFirstName(res.data.firstName);
          setLastName(res.data.lastName);
          setEmail(res.data.email);
          setLocation(res.data.location);
          setPosition(res.data.position);
          setHours(res.data.hours);
        })
        .catch((e) => console.log(e));
    } else {
      console.error("UserId not found");
    }
  }, []);

  return (
    <div>
      <div className="container12">
        <div className="title">
          {" "}
          <h2 className="text-white ">Employee</h2>
        </div>
        <div className="card-body">
          <form>
            <div className="form-group">
              <input
                className="form-control"
                value={firstName}
                onChange={(e) => setFirstName(e.target.value)}
                type="text"
                placeholder="Enter First Name"
              />
            </div>
            <div className="form-group mb-2">
              <input
                className="form-control"
                value={lastName}
                onChange={(e) => setLastName(e.target.value)}
                type="text"
                placeholder="Enter Last Name"
              />
            </div>
            <div className="form-group mb-2">
              <input
                className="form-control"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                type="email"
                placeholder="Enter Email"
              />
            </div>
            <div className="form-group mb-2">
              <input
                className="form-control"
                value={location}
                onChange={(e) => setLocation(e.target.value)}
                type="location"
                placeholder="Enter Location"
              />
            </div>
            <div className="form-group mb-2">
              <input
                className="form-control"
                value={position}
                onChange={(e) => setPosition(e.target.value)}
                type="Position"
                placeholder="Enter Position"
              />
            </div>
            <div className="form-group mb-2">
              <input
                className="form-control"
                value={hours}
                onChange={(e) => setHours(e.target.value)}
                type="hours"
                placeholder="Enter hours"
              />
            </div>
            <div className="button">
            <button
              onClick={(e) => saveEmployee(e)}
              className="custom-btn btn-5"
            >
              <span>Save</span>
            </button>{" "}
            <Link to={`/user/${userId}/employees`} style={{ textDecoration: "none" }} href="">
              <button className="custom-btn btn-13">Cancel</button>
              </Link>
              </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default AddEmployeeComponent;
